package Task4controlStatementInJava;

public class Quest1IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=30;
		int b=20;
		int c=10;
		int d=5;
		int sum1=a+b;
		int sum2=c+d;
		
		if(sum1>sum2)
		{
			System.out.println("Sum of a and b is greater than sum of c and d");
		}
		else
		{
			System.out.println("Sum of and b is not greater than c and d");
		}
	}

}
